SELECT O.ORDER_ID -- 오더아이디 , O.ORDER_NUMBER -- 주문번호 , O.ORDER_DT -- 주문일 , CASE -- 주문한 사람 WHEN M.MEMBER_ID IS NULL THEN '비회원' ELSE M.MEMBER_ID END AS USER_ID , O.TOTAL_AMOUNT -- 총가격 , O.DISCOUNT_AMOUNT -- 할인가격 , O.FINAL_AMOUNT -- 최종가격 , O.ORDER_COMMENT -- 주문상태 , P.PAYMENT_STATUS -- 결제상태(입금시) , S.SHIPMENT_STATUS -- 배송상태 , C.COUPON_TYPE -- 어떤쿠폰인지 , PS.PROMOTION_NAME -- 어떤프로모션인지 , PT.POINT -- 사용 포인트 FROM ORDERS O LEFT JOIN PAYMENTS P ON O.ORDER_ID = P.ORDER_ID LEFT JOIN SHIPS S ON O.ORDER_ID = S.ORDER_ID LEFT JOIN MEMBERS M ON O.PHONE_NUMBER = M.PHONE_NUMBER LEFT JOIN PROMOTION_LOG PL ON O.ORDER_ID = PL.ORDER_ID LEFT JOIN PROMOTIONS PS ON PL.PROMOTION_ID = PS.PROMOTION_ID LEFT JOIN COUPONS C ON O.ORDER_ID = C.ORDER_ID LEFT JOIN POINTS PT ON O.ORDER_ID = PT.ORDER_ID AND PT.CHANGE_TYPE = '사용' WHERE 1=1
AND O.ORDER_DT >= #{START_DATE}
AND O.ORDER_DT <= #{END_DATE}
AND (M.MEMBER_ID LIKE CONCAT('%', #{USER_ID}, '%') OR (#{USER_ID} = '비회원' AND M.MEMBER_ID IS NULL))
AND O.ORDER_STATUS = #{ORDER_STATUS}
AND P.PAYMENT_STATUS = #{PAYMENT_STATUS}
#결제내역 조회
SELECT PAYMENT_ID -- 결제 ID , '0' AS CHK -- 넥사 체크박스 설정을 위한 CHK , ORDER_ID -- 주문 ID , PAYMENT_METHOD -- 결제 방법 , PAYMENT_AMOUNT -- 결제 금액 , PAYMENT_STATUS -- 결제 상태 , PAYMENT_DT -- 결제 일자 , UPDATE_DT -- 수정 일자 FROM PAYMENTS WHERE 1=1
AND P.PAYMENT_STATUS = #{PAYMENT_STATUS}
