UPDATE PAYMENTS -- 결제테이블 SET PAYMENT_STATUS = #{PAYMENT_STATUS} -- 결제 상황 업데이트 WHERE PAYMENT_ID = #{PAYMENT_ID} -- 결제ID로 조회
#결제내역 조회
SELECT SHIPMENT_ID -- 배송 ID , '0' AS CHK -- 넥사 체크박스 설정을 위한 CHK , ORDER_ID -- 주문 ID , COUIER_NAME -- 택배 회사 , TRACKING_NUMBER -- 송장 번호 , SHIPMENT_STATUS -- 배송 상태 , SHIPPED_DT -- 결제 일자 , DELIVERD_DT -- 배송 완료 일자 , UPDATE_DT -- 수정 일자 FROM SHIPS WHERE 1=1
AND P.PAYMENT_STATUS = #{PAYMENT_STATUS}
