mapper.select:
SELECT p.PRODUCT_CODE, p.PRODUCT_NAME, p.PRODUCT_CONTENT, p.COST_PRICE, p.PRODUCT_PRICE, o.ADDITIONAL_PRICE, CASE WHEN p.SALED_PRICE IS NULL OR p.SALED_PRICE = '' THEN (p.PRODUCT_PRICE + IFNULL(o.ADDITIONAL_PRICE,0)) ELSE p.SALED_PRICE END AS TOTAL_PRICE, mc.MAIN_CATE_NM, sc.SUB_CATE_NM, o.OPTION_NAME, p.IS_VISIBLE, i.QUANTITY, CASE WHEN i.QUANTITY = 0 THEN 'OUT' ELSE '' END AS SOLD_OUT, p.INPUT_DT, p.UPDATE_DT FROM PRODUCTS p JOIN SUB_CATEGORIES sc ON p.SUB_CATE_ID = sc.SUB_CATE_ID JOIN MAIN_CATEGORIES mc ON sc.MAIN_CATE_ID = mc.MAIN_CATE_ID LEFT JOIN OPTIONS o ON p.PRODUCT_ID = o.PRODUCT_ID LEFT JOIN INVENTORIES i ON p.PRODUCT_ID = i.PRODUCT_ID WHERE 1=1
AND p.PRODUCT_CODE LIKE CONCAT('%', #{SEARCH_TEXT}, '%')
AND p.PRODUCT_NAME LIKE CONCAT('%', #{SEARCH_TEXT}, '%')
AND p.IS_VISIBLE = #{IS_VISIBLE}
AND i.QUANTITY > 0
AND i.QUANTITY = 0
AND COALESCE(p.UPDATE_DT, p.INPUT_DT) >= #{START_DATE}
AND COALESCE(p.UPDATE_DT, p.INPUT_DT) <= #{END_DATE}
AND mc.MAIN_CATE_ID = #{MAIN_CATE_ID}
AND sc.SUB_CATE_ID = #{SUB_CATE_ID}
ORDER BY p.INPUT_DT DESC

mapper.update:
UPDATE product_img
SET PRODUCT_ID = #{PRODUCT_ID}, UPDATE_DT = NOW(), UPDATE_ID = #{UPDATE_ID}
WHERE PRODUCT_ID = 0

UPDATE product_img
SET IS_MAIN = 'Y'
WHERE PRODUCT_ID = #{PRODUCT_ID}
  AND IMAGE_ID = (
    SELECT t.IMAGE_ID FROM (
      SELECT IMAGE_ID FROM product_img WHERE PRODUCT_ID = #{PRODUCT_ID} ORDER BY IMAGE_ID ASC LIMIT 1
    ) t
  )
